    private static int solution2(int[] nums) {
        int left = 0;
        int right = 0;
        int i = 0;

        int maxConsecutive = 0;
        while (i<nums.length){
            while (i<nums.length && nums[i]!=1) i++;
            left = i;
            while (i<nums.length && nums[i]==1) i++;
            right = i;
            maxConsecutive = Math.max(maxConsecutive,right-left);
            i++;
        }
        return maxConsecutive;



    }

    private static int solution(int[] nums) {
        if (nums==null)return 0;
        if (nums.length==1){
            if (nums[0]==1)return 1;
            else return 0;
        }
        int s1 = 0;
        int s2 = nums.length-1;
        int ss1 = 0;
        int ss2 = 0;
        int max_ss1 = 0;
        int max_ss2 = 0;
        while(s1<s2){
            if (nums[s1]==1){
                s1++;
                ss1++;
            }else{
                s1++;
                if (ss1>max_ss1) max_ss1 =ss1;
                ss1 = 0;
            }
            if (nums[s2]==1){
                s2--;
                ss2++;
            }else{
                s2--;
                if (ss2>max_ss2) max_ss2 =ss2;
                ss2 = 0;
            }
        }
        int res = 0;
        System.out.println(nums.length);
        System.out.println("ss1:"+ss1);
        System.out.println("ss2:"+ss2);
        if(nums.length%2==1){
        if (s1+1==1){res= ss2+ss1;}
        else{if(ss1>ss2)res=ss1;else res =ss2; }}
        else{
            res= ss2+ss1;
        }
        int max = (max_ss1>max_ss2)? max_ss1:max_ss2;
        max = (max>res)? max:res;
        return max;
    }


